customProfile.css:
:root {
  /* Typography */
  --gp-font-heading: "Inter", system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
  --gp-font-body: var(--gp-font-heading);

  /* Design Tokens – Colors */
  --gp-color-primary: #2563eb;   /* Indigo-600 */
  --gp-color-accent: #10b981;    /* Emerald-500 */
  --gp-color-text:   #1f2937;    /* Gray-800  */
  --gp-color-muted:  #6b7280;    /* Gray-500  */
  --gp-color-border: #e5e7eb;    /* Gray-200  */
  --gp-color-bg:     #ffffff;    /* White     */
  --gp-color-surface:#f9fafb;    /* Gray-50   */

  /* Other Tokens */
  --gp-radius: 0.75rem;          /* 12px rounded */
  --gp-ease:  cubic-bezier(0.4, 0, 0.2, 1);
}

/* Container */
.garden-profile {
  max-width: 960px;
  margin-inline: auto;
  padding: 3rem 1.5rem;
  background: var(--gp-color-bg);
  border-radius: var(--gp-radius);
  box-shadow: 0 4px 24px rgba(0, 0, 0, 0.06);
}

/* Header */
.header {
  text-align: center;
  margin-bottom: 3rem;
}

.header h1 {
  font-family: var(--gp-font-heading);
  font-size: clamp(2rem, 5vw, 3.25rem);
  font-weight: 700;
  color: var(--gp-color-text);
  margin-bottom: 0.5rem;
}

.location {
  font-size: 1.125rem;
  color: var(--gp-color-muted);
}

/* Section Headings */
.section {
  margin-bottom: 3rem;
  animation: fade-up 0.6s var(--gp-ease) both;
  animation-delay: 0.15s;
}

.section h2 {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1.5rem;
  font-weight: 600;
  font-family: var(--gp-font-heading);
  color: var(--gp-color-text);
  position: relative;
}

.section h2::before {
  content: "";
  width: 4px;
  height: 100%;
  background: linear-gradient(to bottom, var(--gp-color-primary), var(--gp-color-accent));
  border-radius: 6px;
}

/* List Styling */
.amenities-list,
.rules-list {
  list-style: none;
  padding: 0;
  margin-top: 1rem;
}

.amenities-list li,
.rules-list li {
  position: relative;
  padding: 0.65rem 0 0.65rem 2rem;
  line-height: 1.45;
  color: var(--gp-color-text);
  font-size: 1rem;
  transition: background 0.3s var(--gp-ease);
}

.amenities-list li:hover,
.rules-list li:hover {
  background: rgba(59, 130, 246, 0.05);
}

/* Amenity bullet – circle */
.amenities-list li::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0.9rem;
  width: 0.65rem;
  height: 0.65rem;
  border-radius: 50%;
  background: var(--gp-color-accent);
  box-shadow: 0 0 0 3px rgba(16, 185, 129, 0.25);
}

/* Rule bullet – triangle */
.rules-list li::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0.9rem;
  width: 0.5rem;
  height: 0.5rem;
  background: var(--gp-color-primary);
  clip-path: polygon(50% 0%, 0% 100%, 100% 100%);
}

/* Info Grid */
.info-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1.75rem;
  margin-top: 2rem;
  padding: 2rem;
  background: var(--gp-color-surface);
  border-radius: var(--gp-radius);
}

.info-item {
  text-align: center;
}

.info-item .label {
  font-size: 0.875rem;
  color: var(--gp-color-muted);
  margin-bottom: 0.25rem;
}

.info-item .value {
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--gp-color-text);
  transition: color 0.3s var(--gp-ease);
}

.info-item:hover .value {
  color: var(--gp-color-primary);
}

/* Fade‑Up Animation */
@keyframes fade-up {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Dark‑Mode Support */
@media (prefers-color-scheme: dark) {
  :root {
    --gp-color-text:   #f3f4f6; /* Gray‑100 */
    --gp-color-muted:  #9ca3af; /* Gray‑400 */
    --gp-color-bg:     #1f2937; /* Gray‑800 */
    --gp-color-surface:#374151; /* Gray‑700 */
    --gp-color-border: #4b5563; /* Gray‑600 */
  }

  .garden-profile {
    box-shadow: none;
  }
}

/* Small Screens */
@media (max-width: 480px) {
  .header h1 {
    font-size: 2rem;
  }

  .section h2 {
    font-size: 1.25rem;
  }
}

