  box-shadow: 0 4px 24px rgba(0, 0, 0, 0.06);
}

/* Header */
.header {
  text-align: center;
  margin-bottom: 3rem;
}

.header h1 {
  font-family: var(--gp-font-heading);
  font-size: clamp(2rem, 5vw, 3.25rem);
  font-weight: 700;
  color: var(--gp-color-text);
  margin-bottom: 0.5rem;
}

.location {
  font-size: 1.125rem;
  color: var(--gp-color-muted);
}

/* Section Headings */
.section {
  margin-bottom: 3rem;
  animation: fade-up 0.6s var(--gp-ease) both;
  animation-delay: 0.15s;
}

.section h2 {
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1.5rem;
  font-weight: 600;
  font-family: var(--gp-font-heading);
  color: var(--gp-color-text);
  position: relative;
}

.section h2::before {
  content: "";
  width: 4px;
  height: 100%;
  background: linear-gradient(to bottom, var(--gp-color-primary), var(--gp-color-accent));
  border-radius: 6px;
}

/* List Styling */
.amenities-list,
.rules-list {
  list-style: none;
  padding: 0;
  margin-top: 1rem;
}

.amenities-list li,
.rules-list li {
  position: relative;
  padding: 0.65rem 0 0.65rem 2rem;
  line-height: 1.45;
  color: var(--gp-color-text);
  font-size: 1rem;
  transition: background 0.3s var(--gp-ease);
}

.amenities-list li:hover,
.rules-list li:hover {
  background: rgba(59, 130, 246, 0.05);
}

/* Amenity bullet – circle */
.amenities-list li::before {
  content: "";
  position: absolute;
  left: 0;
  top: 0.9rem;
  width: 0.65rem;
  height: 0.65rem;
  border-radius: 50%;
  background: var(--gp-color-accent);
  box-shadow: 0 0 0 3px rgba(16, 185, 129, 0.25);
